mapper.delete:
delete
from tbl_board
where bno = #{bno};
set
@cnt = 0;
update tbl_board
set bno =@cnt:=@cnt +1;
alter table tbl_board auto_increment = 1;

mapper.select:

set
@rownum = 0;

select rn, bno, title, content, writer, regdate, updatedate
from (select @rownum:=@rownum + 1 rn, bno, title, content, writer, regdate, updatedate
      from tbl_board
      where @rownum
          < #{rowNum}) rn
where rn
          > (#{rn});
